
public class VariableExample {
	
	/*
	 * Type - Primitive (8개)
	 *          1. 수치
	 *             - 정수
	 *                 byte
	 *                 short
	 *                 char
	 *                 int
	 *                 long
	 *             - 실수
	 *                 float
	 *                 double
	 *          2. 논리
	 *             - boolean
	 *             
	 *      - Reference
	 *             무한대로 많다
	 */
	

	public static void main(String[] args) {
		
		int num = 300;    // 변수 : 메모리 공간의 이름  "=" 대입연산자
		num = num + 100;
		System.out.println(num);
		// TODO Auto-generated method stub
		
		//Primitive 타입 변수 8개 변수 선언문 
		//정수
		byte b;   // 1byte   -128~127
		short s;  // 2byte   
		char c;   // 2byte
		int i;    // 4byte
		long l;   // 8byte
        //실수   
		float f;  // 4byte
		double d; // 8byte
		//논리
		boolean tf; // 1byte

	}

}
